void Sphere::draw(Shader * shader) {
    if (shader->name == "TEXTURE")
        shader = Renderer::colorShader;
    shader->use();
    bindBuffers();
    modelMatrix = glm::rotate(baseModel->modelMatrix, glm::radians(angleVector.y), glm::vec3(0.0, 1.0, 0.0));
    modelMatrix = glm::translate(modelMatrix, scaleFactor * translateVector);
    shader->setMat4("model", glm::scale(modelMatrix, scaleFactor * scaleVector));
    if (Renderer::renderingMode == GL_TRIANGLES) {
        glDrawArrays(GL_TRIANGLE_STRIP, 0, nbOfVertices);
    } else {
        glDrawArrays(Renderer::renderingMode, 0, nbOfVertices);
    }
}
void Sphere::bindBuffers() {
    // Binding array first then buffers
    glBindVertexArray(VAO);
    glBindBuffer(GL_ARRAY_BUFFER, VBO);
}

void Sphere::update(std::string attribute, float value) {
    if (attribute == "SCALE_FACTOR") {
        scaleFactor= value;
    }
}
void Sphere::reset() {
    scaleFactor= 1;
    angleVector.y = 0.0f;
}
void Sphere::animate() {
    if (animation) {
        if (angleVector.y>=45.0f ) {
            forward = false;
        } else if (angleVector.y<=-45.0f){
            forward = true;
        }
        if (forward) {
            angleVector.y += 6.0f;
        } else {
            angleVector.y -= 6.0f;
        }
        
    }
    
}
